// Function to calculate the current year plus a user's age
function calculateAge(age) {
  const currentYear = new Date().getFullYear();
  const totalAge = currentYear + age;
  return totalAge;
}

// Get the user's age from the prompt
const userAge = parseFloat(prompt("Enter your age: "));

// Calculate the current year plus the user's age
const totalAge = calculateAge(userAge);

// Display the result
alert("Your current year plus your age is: " + totalAge);
