// Function to calculate the current year plus a user's age
function calculateAge(age) {
  // Get the current year
  const currentYear = new Date().getFullYear();

  // Calculate the current year plus the user's age
  const result = currentYear + age;

  // Return the result
  return result;
}

// Get the user's age
const userAge = prompt("Enter your age:");

// Calculate the current year plus the user's age
const result = calculateAge(userAge);

// Display the result
alert("Your current year plus your age is: " + result);
